import React, { Component } from "react";
import "./Navigation.css";

class Navigation extends Component {
  constructor() {
    super();
    this.state = {
      scrollTop: 0,
    };
  }
  componentDidMount = () => {
    window.addEventListener("scroll", this.handleScroll);
  };

  componentWillUnMount = () => {
    window.removeEventListener("scroll", this.handleScroll);
  };
  handleScroll = (e) => {
    const scrollTop = ("scroll", e.srcElement.scrollingElement.scrollTop);
    console.log(scrollTop);
    const NavAfterStyle = {
      width: "99%",
      height: "100px",
      background: "var(--maincolor)",
      margin: "auto",
      display: "flex",
      alignitems: "center",
      justifycontent: "center",
      position: "fixed",
      margin: "0 auto",
      left: 0,
      right: 0,
      zindex: 2,
    };
    if (scrollTop >= 512) {
    }
    this.setState({
      scrollTop,
    });
  };
  render() {
    return (
      <section id="navigation">
        <div className="navigation-container" onScroll={this.handleScroll}>
          <div className="nav-logo-img">
            <img
              src="https://image.flaticon.com/icons/png/128/639/639394.png"
              alt="logo"
              title="logo"
            />
          </div>
          <div className="nav-item-container">
            <div className="nav-item font-style">프로젝트</div>
            <div className="nav-item font-style">소개</div>
          </div>
        </div>
      </section>
    );
  }
}

export default Navigation;
